hudson.tasks.Shell.command:
#!/bin/bash
# Create artifacts dir export artifacts_dir=$WORKSPACE/.artifacts rm -rf $artifacts_dir mkdir -p $artifacts_dir touch $artifacts_dir/.ignore
admin=crowbar$hw_number cloud=qa$hw_number export $rally_server export $jeos-rally scp root@$admin:/root/scripts/jenkins-support . source ./jenkins-support connect_rally_server_run_test ret=$? # Copy test run reslust output to jenkins artifacts scp root@$rally_server:/root/results/* $artifacts_dir exit $ret
